mapper.update:
UPDATE product_cart 
SET cnt = cnt + 1
WHERE cart_no = #{cart_no}

UPDATE product_cart 
SET cnt = cnt - 1
WHERE cart_no = #{cart_no}

mapper.delete:
DELETE FROM product_cart
